package Beakjoon_C;

import java.util.Scanner;

public class C_AplusB_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner sc = new Scanner(System.in);
		int a = sc.nextInt();
		for(int i=0; i<a; i++)
		{
			int c = sc.nextInt();
			int d = sc.nextInt();
			System.out.println(c+d);
		}
	}
}
