package Beakjoon_C;

import java.util.Scanner;

public class C_AplusB_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner sc = new Scanner(System.in);
		int x, y;
		while(true)
		{
			x = sc.nextInt();
			y = sc.nextInt();
			
			if(x==0)
			{
				break;
			}
			System.out.println(x+y);
			
		}
		
		
	}

}
